export declare const allViewsSQL: readonly ["\nCREATE VIEW IF NOT EXISTS product_inventory_status AS\nSELECT\n  p.product_id,\n  p.sku,\n  p.name,\n  p.description,\n  p.item_type,\n  p.is_active,\n\n  p.category_id,\n  c.name AS category_name,\n\n  ps.quantity_on_hand,\n  ps.quantity_available,\n  ps.quantity_reserved,\n  ps.quantity_on_purchase_order,\n  ps.quantity_on_work_order,\n  ps.quantity_in_transit,\n  ps.quantity_buildable,\n\n  rs.reorder_point,\n  rs.reorder_quantity,\n  rs.enable_reordering,\n  rs.vendor_id AS preferred_vendor_id,\n\n  CASE\n    WHEN rs.enable_reordering = 1\n      AND rs.reorder_point IS NOT NULL\n      AND CAST(COALESCE(ps.quantity_available, '0') AS REAL) <= CAST(rs.reorder_point AS REAL)\n    THEN 1\n    ELSE 0\n  END AS is_below_reorder,\n\n  ps.image_small_url AS image_url\n\nFROM products p\nLEFT JOIN categories c ON p.category_id = c.category_id\nLEFT JOIN product_summary ps ON p.product_id = ps.product_id AND ps.location_id IS NULL\nLEFT JOIN reorder_settings rs ON p.product_id = rs.product_id AND rs.location_id IS NULL;\n", "\nCREATE VIEW IF NOT EXISTS reorder_alerts AS\nSELECT\n  p.product_id,\n  p.sku,\n  p.name AS product_name,\n  p.category_id,\n\n  ps.quantity_on_hand,\n  ps.quantity_available,\n  ps.quantity_on_purchase_order,\n\n  rs.reorder_point,\n  rs.reorder_quantity,\n\n  CAST(rs.reorder_point AS REAL) - CAST(COALESCE(ps.quantity_available, '0') AS REAL) AS shortfall_quantity,\n\n  COALESCE(rs.reorder_quantity,\n    CAST(rs.reorder_point AS REAL) - CAST(COALESCE(ps.quantity_available, '0') AS REAL)\n  ) AS suggested_order_quantity,\n\n  rs.vendor_id AS preferred_vendor_id,\n  v.name AS vendor_name,\n  v.vendor_code,\n\n  vi.vendor_item_code,\n  vi.cost AS vendor_cost,\n  vi.lead_time_days,\n\n  CAST(COALESCE(rs.reorder_quantity,\n    CAST(rs.reorder_point AS REAL) - CAST(COALESCE(ps.quantity_available, '0') AS REAL)\n  ) AS REAL) * CAST(COALESCE(vi.cost, '0') AS REAL) AS estimated_order_value\n\nFROM products p\nINNER JOIN reorder_settings rs ON p.product_id = rs.product_id AND rs.location_id IS NULL\nLEFT JOIN product_summary ps ON p.product_id = ps.product_id AND ps.location_id IS NULL\nLEFT JOIN vendors v ON rs.vendor_id = v.vendor_id\nLEFT JOIN vendor_items vi ON rs.vendor_id = vi.vendor_id AND p.product_id = vi.product_id\nWHERE rs.enable_reordering = 1\n  AND rs.reorder_point IS NOT NULL\n  AND CAST(COALESCE(ps.quantity_available, '0') AS REAL) <= CAST(rs.reorder_point AS REAL)\n  AND p.is_active = 1;\n", "\nCREATE VIEW IF NOT EXISTS open_orders_unified AS\nSELECT\n  po.purchase_order_id AS order_id,\n  'purchase_order' AS order_type,\n  po.order_number,\n  po.status,\n  po.order_date,\n  po.expected_date,\n  po.vendor_id AS counterparty_id,\n  v.name AS counterparty_name,\n  'vendor' AS counterparty_type,\n  po.location_id,\n  l.name AS location_name,\n  po.total,\n  po.remarks,\n  po.last_modified_date_time AS last_modified\nFROM purchase_orders po\nLEFT JOIN vendors v ON po.vendor_id = v.vendor_id\nLEFT JOIN locations l ON po.location_id = l.location_id\nWHERE po.status NOT IN ('Completed', 'Cancelled', 'Closed')\n\nUNION ALL\n\nSELECT\n  so.sales_order_id AS order_id,\n  'sales_order' AS order_type,\n  so.order_number,\n  so.status,\n  so.order_date,\n  so.expected_ship_date AS expected_date,\n  so.customer_id AS counterparty_id,\n  c.name AS counterparty_name,\n  'customer' AS counterparty_type,\n  so.location_id,\n  l.name AS location_name,\n  so.total,\n  so.remarks,\n  so.last_modified_date_time AS last_modified\nFROM sales_orders so\nLEFT JOIN customers c ON so.customer_id = c.customer_id\nLEFT JOIN locations l ON so.location_id = l.location_id\nWHERE so.status NOT IN ('Completed', 'Cancelled', 'Closed', 'Shipped')\n\nUNION ALL\n\nSELECT\n  mo.manufacturing_order_id AS order_id,\n  'manufacturing_order' AS order_type,\n  mo.order_number,\n  mo.status,\n  mo.order_date,\n  mo.expected_date,\n  mo.product_id AS counterparty_id,\n  p.name AS counterparty_name,\n  'product' AS counterparty_type,\n  mo.location_id,\n  l.name AS location_name,\n  CAST(mo.quantity AS TEXT) AS total,\n  mo.remarks,\n  mo.last_modified_date_time AS last_modified\nFROM manufacturing_orders mo\nLEFT JOIN products p ON mo.product_id = p.product_id\nLEFT JOIN locations l ON mo.location_id = l.location_id\nWHERE mo.status NOT IN ('Completed', 'Cancelled', 'Closed');\n", "\nCREATE VIEW IF NOT EXISTS inventory_by_location AS\nSELECT\n  ps.product_id,\n  ps.location_id,\n\n  p.sku,\n  p.name AS product_name,\n  p.item_type,\n  p.is_active,\n  p.category_id,\n  c.name AS category_name,\n\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n  l.is_shippable,\n  l.is_receivable,\n\n  ps.quantity_on_hand,\n  ps.quantity_available,\n  ps.quantity_reserved,\n  ps.quantity_reserved_for_sales,\n  ps.quantity_reserved_for_manufacturing,\n  ps.quantity_reserved_for_transfers,\n  ps.quantity_on_purchase_order,\n  ps.quantity_on_work_order,\n  ps.quantity_on_transfer_order,\n  ps.quantity_picked,\n  ps.quantity_in_transit,\n\n  ps.image_small_url AS image_url\n\nFROM product_summary ps\nINNER JOIN products p ON ps.product_id = p.product_id\nINNER JOIN locations l ON ps.location_id = l.location_id\nLEFT JOIN categories c ON p.category_id = c.category_id\nWHERE ps.location_id IS NOT NULL;\n", "\nCREATE VIEW IF NOT EXISTS location_stock_summary AS\nSELECT\n  l.location_id,\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n  l.is_shippable,\n  l.is_receivable,\n  COUNT(DISTINCT ps.product_id) AS sku_count,\n  SUM(CAST(COALESCE(ps.quantity_on_hand, '0') AS REAL)) AS total_quantity_on_hand,\n  SUM(CAST(COALESCE(ps.quantity_available, '0') AS REAL)) AS total_quantity_available,\n  SUM(CAST(COALESCE(ps.quantity_reserved, '0') AS REAL)) AS total_quantity_reserved,\n  SUM(CAST(COALESCE(ps.quantity_in_transit, '0') AS REAL)) AS total_quantity_in_transit\nFROM locations l\nLEFT JOIN product_summary ps ON l.location_id = ps.location_id\nWHERE l.is_active = 1\nGROUP BY l.location_id, l.name, l.abbreviation, l.is_shippable, l.is_receivable;\n", "\nCREATE VIEW IF NOT EXISTS location_reorder_alerts AS\nSELECT\n  p.product_id,\n  p.sku,\n  p.name AS product_name,\n  p.category_id,\n\n  rs.location_id,\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n\n  ps.quantity_on_hand,\n  ps.quantity_available,\n  ps.quantity_on_purchase_order,\n  ps.quantity_in_transit,\n\n  rs.reorder_point,\n  rs.reorder_quantity,\n\n  CAST(rs.reorder_point AS REAL) - CAST(COALESCE(ps.quantity_available, '0') AS REAL) AS shortfall_quantity,\n\n  COALESCE(rs.reorder_quantity,\n    CAST(rs.reorder_point AS REAL) - CAST(COALESCE(ps.quantity_available, '0') AS REAL)\n  ) AS suggested_order_quantity,\n\n  rs.vendor_id AS preferred_vendor_id,\n  v.name AS vendor_name,\n  v.vendor_code,\n\n  vi.vendor_item_code,\n  vi.cost AS vendor_cost,\n  vi.lead_time_days,\n\n  rs.from_location_id\n\nFROM products p\nINNER JOIN reorder_settings rs ON p.product_id = rs.product_id\nINNER JOIN locations l ON rs.location_id = l.location_id\nLEFT JOIN product_summary ps ON p.product_id = ps.product_id AND rs.location_id = ps.location_id\nLEFT JOIN vendors v ON rs.vendor_id = v.vendor_id\nLEFT JOIN vendor_items vi ON rs.vendor_id = vi.vendor_id AND p.product_id = vi.product_id\nWHERE rs.enable_reordering = 1\n  AND rs.reorder_point IS NOT NULL\n  AND rs.location_id IS NOT NULL\n  AND p.is_active = 1\n  AND CAST(COALESCE(ps.quantity_available, '0') AS REAL) <= CAST(rs.reorder_point AS REAL);\n", "\nCREATE VIEW IF NOT EXISTS transfer_pipeline AS\nSELECT\n  st.stock_transfer_id,\n  st.transfer_number,\n  st.status,\n  st.transfer_date,\n  st.remarks,\n  st.last_modified_date_time AS last_modified,\n\n  st.from_location_id,\n  from_loc.name AS from_location_name,\n  from_loc.abbreviation AS from_location_abbreviation,\n\n  st.to_location_id,\n  to_loc.name AS to_location_name,\n  to_loc.abbreviation AS to_location_abbreviation,\n\n  stl.stock_transfer_line_id,\n  stl.line_num,\n  stl.product_id,\n  p.sku,\n  p.name AS product_name,\n  stl.quantity,\n  stl.from_sublocation,\n  stl.to_sublocation\n\nFROM stock_transfers st\nINNER JOIN stock_transfer_lines stl ON st.stock_transfer_id = stl.stock_transfer_id\nLEFT JOIN products p ON stl.product_id = p.product_id\nLEFT JOIN locations from_loc ON st.from_location_id = from_loc.location_id\nLEFT JOIN locations to_loc ON st.to_location_id = to_loc.location_id\nWHERE st.status NOT IN ('Completed', 'Cancelled', 'Closed');\n", "\nCREATE VIEW IF NOT EXISTS inventory_detail AS\nSELECT\n  il.inventory_line_id,\n\n  il.product_id,\n  p.sku,\n  p.name AS product_name,\n  p.item_type,\n  p.category_id,\n  c.name AS category_name,\n\n  il.location_id,\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n\n  il.sublocation,\n\n  il.serial,\n  il.lot_id,\n\n  il.quantity_on_hand,\n\n  p.track_serials,\n  p.track_lots,\n  p.track_expiry,\n  p.shelf_life_days\n\nFROM inventory_lines il\nINNER JOIN products p ON il.product_id = p.product_id\nLEFT JOIN locations l ON il.location_id = l.location_id\nLEFT JOIN categories c ON p.category_id = c.category_id;\n", "\nCREATE VIEW IF NOT EXISTS stock_movement_ledger AS\n\n-- PO Receipts (inventory IN)\nSELECT\n  'PO_RECEIPT' AS movement_type,\n  po.order_date AS movement_date,\n  pol.product_id,\n  p.sku,\n  p.name AS product_name,\n  po.location_id,\n  l.name AS location_name,\n  pol.quantity_received AS quantity,\n  'PurchaseOrder' AS reference_type,\n  po.order_number AS reference_number,\n  po.purchase_order_id AS reference_id,\n  pol.purchase_order_line_id AS line_id\nFROM purchase_order_lines pol\nINNER JOIN purchase_orders po ON pol.purchase_order_id = po.purchase_order_id\nINNER JOIN products p ON pol.product_id = p.product_id\nLEFT JOIN locations l ON po.location_id = l.location_id\nWHERE CAST(COALESCE(pol.quantity_received, '0') AS REAL) > 0\n\nUNION ALL\n\n-- SO Shipments (inventory OUT - negative quantity)\nSELECT\n  'SO_SHIPMENT' AS movement_type,\n  so.order_date AS movement_date,\n  sol.product_id,\n  p.sku,\n  p.name AS product_name,\n  so.location_id,\n  l.name AS location_name,\n  '-' || sol.quantity_shipped AS quantity,\n  'SalesOrder' AS reference_type,\n  so.order_number AS reference_number,\n  so.sales_order_id AS reference_id,\n  sol.sales_order_line_id AS line_id\nFROM sales_order_lines sol\nINNER JOIN sales_orders so ON sol.sales_order_id = so.sales_order_id\nINNER JOIN products p ON sol.product_id = p.product_id\nLEFT JOIN locations l ON so.location_id = l.location_id\nWHERE CAST(COALESCE(sol.quantity_shipped, '0') AS REAL) > 0\n\nUNION ALL\n\n-- Transfers OUT (from source location - negative)\nSELECT\n  'TRANSFER_OUT' AS movement_type,\n  st.transfer_date AS movement_date,\n  stl.product_id,\n  p.sku,\n  p.name AS product_name,\n  st.from_location_id AS location_id,\n  l.name AS location_name,\n  '-' || stl.quantity AS quantity,\n  'StockTransfer' AS reference_type,\n  st.transfer_number AS reference_number,\n  st.stock_transfer_id AS reference_id,\n  stl.stock_transfer_line_id AS line_id\nFROM stock_transfer_lines stl\nINNER JOIN stock_transfers st ON stl.stock_transfer_id = st.stock_transfer_id\nINNER JOIN products p ON stl.product_id = p.product_id\nLEFT JOIN locations l ON st.from_location_id = l.location_id\nWHERE st.status IN ('Completed', 'Closed')\n\nUNION ALL\n\n-- Transfers IN (to destination location - positive)\nSELECT\n  'TRANSFER_IN' AS movement_type,\n  st.transfer_date AS movement_date,\n  stl.product_id,\n  p.sku,\n  p.name AS product_name,\n  st.to_location_id AS location_id,\n  l.name AS location_name,\n  stl.quantity AS quantity,\n  'StockTransfer' AS reference_type,\n  st.transfer_number AS reference_number,\n  st.stock_transfer_id AS reference_id,\n  stl.stock_transfer_line_id AS line_id\nFROM stock_transfer_lines stl\nINNER JOIN stock_transfers st ON stl.stock_transfer_id = st.stock_transfer_id\nINNER JOIN products p ON stl.product_id = p.product_id\nLEFT JOIN locations l ON st.to_location_id = l.location_id\nWHERE st.status IN ('Completed', 'Closed')\n\nUNION ALL\n\n-- Adjustments (can be positive or negative)\nSELECT\n  'ADJUSTMENT' AS movement_type,\n  sa.adjustment_date AS movement_date,\n  sal.product_id,\n  p.sku,\n  p.name AS product_name,\n  sa.location_id,\n  l.name AS location_name,\n  sal.quantity AS quantity,\n  'StockAdjustment' AS reference_type,\n  sa.adjustment_number AS reference_number,\n  sa.stock_adjustment_id AS reference_id,\n  sal.stock_adjustment_line_id AS line_id\nFROM stock_adjustment_lines sal\nINNER JOIN stock_adjustments sa ON sal.stock_adjustment_id = sa.stock_adjustment_id\nINNER JOIN products p ON sal.product_id = p.product_id\nLEFT JOIN locations l ON sa.location_id = l.location_id;\n", "\nCREATE VIEW IF NOT EXISTS lot_inventory AS\nSELECT\n  il.inventory_line_id,\n\n  il.product_id,\n  p.sku,\n  p.name AS product_name,\n  p.category_id,\n  c.name AS category_name,\n\n  il.location_id,\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n  il.sublocation,\n\n  il.lot_id,\n  il.quantity_on_hand,\n\n  p.shelf_life_days,\n  p.track_expiry\n\nFROM inventory_lines il\nINNER JOIN products p ON il.product_id = p.product_id\nLEFT JOIN locations l ON il.location_id = l.location_id\nLEFT JOIN categories c ON p.category_id = c.category_id\nWHERE il.lot_id IS NOT NULL AND il.lot_id != '';\n", "\nCREATE VIEW IF NOT EXISTS serial_inventory AS\nSELECT\n  il.inventory_line_id,\n\n  il.product_id,\n  p.sku,\n  p.name AS product_name,\n  p.category_id,\n  c.name AS category_name,\n\n  il.location_id,\n  l.name AS location_name,\n  l.abbreviation AS location_abbreviation,\n  il.sublocation,\n\n  il.serial,\n  il.quantity_on_hand,\n\n  il.lot_id\n\nFROM inventory_lines il\nINNER JOIN products p ON il.product_id = p.product_id\nLEFT JOIN locations l ON il.location_id = l.location_id\nLEFT JOIN categories c ON p.category_id = c.category_id\nWHERE il.serial IS NOT NULL AND il.serial != '';\n"];
